        margin-right: 1em;
    }

    h1 {
        margin-block-start: 0;
        margin-block-end: .5em;
        text-transform: uppercase;
        font-size: 4em;
        font-weight: 100;
    }

    h2 {
        margin-block-end: 1em;
    }

    @media (min-width: 480px) {
        main, footer {
            max-width: 360px;
        }
    }

    @media (min-width: 640px) {
        .app {
            grid-template-columns: 1fr max-content;
            grid-template-rows: unset;
            align-items: stretch;
        }
        main, footer {
            max-width: none;
        }
    }

    main button {
        display: flex;
        align-items: center;
    }

    button span {
        margin-right: 1em;
    }

    a {
        display: inline-block;
        color: unset;
        border: 1px solid transparent;
        border-radius: 2px;
        padding: 2px;
        outline: none;
        color: #4056a1;
    }

    .contact {
        margin-bottom: 2em;
    }

    .contact a {
        text-decoration-line: underline;
        -webkit-text-decoration-thickness: 2px;
        text-decoration-thickness: 2px;
        -webkit-text-underline-offset: 2em;
        text-underline-offset: 4px;
    }

    a:focus {
        outline: none;
        border-color: #c2cbe3;
    }

    input, textarea {
        border-radius: 4px;
        border: none;
        width: 100%;
    }

    textarea {
        resize: none;
    }

    input:focus, textarea:focus {
        outline: none;
        box-shadow: 0 0 0 1px #4056a1;
    }

    .form button {
        display: inline-block;
    }

    button {
        background-color: #4056a1;
        color: #fff;
        text-shadow: 0px 0px #000;
        cursor: pointer;
        margin-right: 1em;
        padding: .4em .8em;
        border-radius: 4px;
        border: none;
    }

    button:disabled {
        cursor: default;
    }

    a:active {
        color: #ef403d;
        text-decoration-color: #ef403d;
    }

    button:focus,
    button:active {
        box-shadow: 0px 0px 0px 2px #c2cbe3;
    }

    menu {
        padding-inline-start: 0;
    }

    img {
        position: absolute;
    }

    .contact form button {
        display: inline-block;
    }
</style>
